  --font-body: [exact value from tokens.fonts.body], sans-serif;

  /* COPY these values EXACTLY from the "space" array in design tokens */
  --space-0: [tokens.space[0]]px;
  --space-1: [tokens.space[1]]px;
  --space-2: [tokens.space[2]]px;
  /* etc for all space values */

  /* COPY these values EXACTLY from the "radii" object in design tokens */
